import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Card, Button, Form, Alert, Spinner } from "react-bootstrap";
import api from "../../services/api";
import "./styles.css";

export default function EditProduct() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  const [data, setData] = useState({
    produto: "",
    qnt: "",
    valor: "",
  });

  useEffect(() => {
    fetchProduct();
  }, [id]);

  const fetchProduct = async () => {
    try {
      setLoading(true);
      const response = await api.get(`/produtos/${id}`);
      setData(response.data);
    } catch (err) {
      setError("Erro ao carregar produto. Tente novamente mais tarde.");
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      setError(null);
      setSuccess(null);

      await api.put(`/produtos/${id}`, data);
      setSuccess("Produto atualizado com sucesso!");
      setTimeout(() => {
        navigate("/produto");
      }, 1500);
    } catch (err) {
      setError("Erro ao atualizar produto. Tente novamente.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="edit-product-container">
      {error && <Alert variant="danger" onClose={() => setError(null)} dismissible>{error}</Alert>}
      {success && <Alert variant="success" onClose={() => setSuccess(null)} dismissible>{success}</Alert>}

      <Card>
        <Card.Header as="h5">EDITAR PRODUTO</Card.Header>
        <Card.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Nome*</Form.Label>
              <Form.Control
                name="produto"
                value={data.produto}
                onChange={handleInputChange}
                placeholder="Nome do produto"
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Quantidade*</Form.Label>
              <Form.Control
                name="qnt"
                type="number"
                min="1"
                value={data.qnt}
                onChange={handleInputChange}
                placeholder="Quantidade"
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Valor*</Form.Label>
              <Form.Control
                name="valor"
                type="number"
                min="0.01"
                step="0.01"
                value={data.valor}
                onChange={handleInputChange}
                placeholder="Valor"
                required
              />
            </Form.Group>

            <div className="button-group">
              <Button
                variant="primary"
                type="submit"
                disabled={loading}
              >
                {loading ? <Spinner animation="border" size="sm" /> : "SALVAR"}
              </Button>
              <Button
                variant="secondary"
                onClick={() => navigate("/produto")}
                disabled={loading}
              >
                CANCELAR
              </Button>
            </div>
          </Form>
        </Card.Body>
      </Card>
    </div>
  );
}
