import { db } from './db';

const api = {
    // Listar todos os produtos
    get: async (endpoint) => {
        if (endpoint === 'lista-produto') {
            return { data: db.getProducts() };
        }
        return { data: [] };
    },

    // Adicionar um novo produto
    post: async (endpoint, data) => {
        if (endpoint === 'produto') {
            return { data: db.addProduct(data) };
        }
        return { data: null };
    },

    // Atualizar um produto
    put: async (endpoint, data) => {
        const id = endpoint.split('/')[1];
        if (endpoint.startsWith('produto/')) {
            return { data: db.updateProduct(id, data) };
        }
        return { data: null };
    },

    // Deletar um produto
    delete: async (endpoint) => {
        const id = endpoint.split('/')[1];
        if (endpoint.startsWith('delete-produto/')) {
            db.deleteProduct(id);
            return { data: { success: true } };
        }
        return { data: null };
    }
};

export default api;
